const items = $input.all();
const warmUpPrompts = [];

for (const item of items) {
  const { title, summary } = item.json;
  // Construct a structured warm-up prompt
  const prompt = `[WARM-UP] Document: ${title}\nContext Summary:\n${summary}\n[END WARM-UP]`;
  warmUpPrompts.push({ json: { prompt } });
}

return warmUpPrompts;

// Simple logging of warm-up status
const { prompt } = $json;
console.log(`Warm-up prompt sent: ${prompt.substring(0, 50)}...`);
return $input.all();

const items = $input.all();
let context = "";

if (!items || items.length === 0 || !items[0].json) {
  return [{ json: { context: "[No relevant context found]", foundContext: false } }];
}

for (const item of items) {
  const { section_title, chunk_text, document_title, document_id } = item.json;
  context += `Document: ${document_title} (ID: ${document_id})\n`;
  context += `Section: ${section_title}\n`;
  context += `Chunk: ${chunk_text}\n\n`;
}

return [{ json: { context: context.trim(), foundContext: true } }];

const userQuery = $input.item.json.query || $workflow.execution.data.initialInput.body.query;
const context = $input.item.json.context;

// Build the final composite prompt
const prompt = `Context:\n${context}\n\nUser Query: ${userQuery}\n\nAnswer:`;

return [{ json: { prompt, userQuery, context } }];

const llmResponse = $input.item.json.content;
const userQuery = $input.item.json.userQuery;

// Process and trim the LLM response
const answer = llmResponse.trim();

return [{ json: { answer, query: userQuery, context: $input.item.json.context } }];

const processingTime = Date.now() - $workflow.execution.startedAt;
const contextLength = $input.item.json.context ? $input.item.json.context.length : 0;

return [{ json: { processingTime, contextLength, query: $input.item.json.query } }];

// Format the facts retrieved from the knowledge graph
// Assuming the knowledge graph returns an array of facts
const items = $input.all();

if (!items || items.length === 0 || !items[0].json) { 
  return [{ json: { facts: '[No relevant facts found]', hasFacts: false } }];
}

let facts = "";
for (const item of items) {
  // Adapt this to the actual structure of your knowledge graph response
  if (item.json.fact) {
    facts += `- ${item.json.fact}\n`;
  } else if (item.json.subject && item.json.predicate && item.json.object) {
    facts += `- ${item.json.subject} ${item.json.predicate} ${item.json.object}\n`;
  }
}

return [{ json: { facts: facts.trim(), hasFacts: true } }];

// Combine dynamic context, knowledge graph facts, and the user query
const userQuery = $input.item.json.query || $workflow.execution.data.initialInput.body.query;
const facts = $input.item.json.facts || "[No relevant facts found]";

let prompt = "";

// Add facts if available
if ($input.item.json.hasFacts) {
  prompt += `Facts:\n${facts}\n\n`;
}

// Append the user query
prompt += `User Query: ${userQuery}\n\nAnswer:`;

return [{ json: { prompt, userQuery, facts } }];

const llmResponse = $input.item.json.content;
const userQuery = $input.item.json.userQuery;
const facts = $input.item.json.facts;

const answer = llmResponse.trim();

return [{ json: { answer, query: userQuery, facts } }];

// Log performance and usage data
const processingTime = Date.now() - $workflow.execution.startedAt;

return [{ json: { processingTime, query: $input.item.json.query, factsUsed: $input.item.json.hasFacts } }];

